mapper.select:
SELECT a.UUID AS uuid, a.PRODUCT_PICTURE AS productPicture, a.COMMODITY_NAME AS commodityName, a.RELEASE_TIME AS releaseTime, a.PRICE AS price, a.USER_ID AS userId, a.STAUS AS staus, b.DELIVERY_ADDRESS as deliveryAddress FROM COMMODITY a LEFT JOIN FRY_USER b ON a.USER_ID = b.UUID WHERE 1 = 1 AND a.USER_ID = #{userId}
AND a.COMMODITY_NAME like concat (concat ('%',#{commodityName}),'%')
ORDER BY STAUS DESC
